definition.script:
import groovy.json.JsonSlurper;
pipeline {
    agent any 
    stages {

        stage('merge global concepts') { 
            steps {
                script {
                    build job: 'ETL - Merge Global Concepts'
                }
            }
        }

        stage('Pull latest stigvars from git') { 
            steps {
                script {
                    build job: 'ETL - Upload Destigmatizing variables to s3'
                }
            }
        }
        stage('merge and generate javabin') { 
            steps {
                script {
                    build job: 'ETL - All Concept Data Merge with Data Analyzer'

                }
                script {
                    build job: 'ETL - Generate Phenotype Javabins Data Analyzer'
                }
            }
        }
        stage('Create dictionary javabin') { 
            steps {
                script {
                    build job: 'ETL - Generate HPDS Data Dictionary Javabin'
                }
            }
        }
        stage('Get updated study counts') { 
            steps {
                script {
                    build job: 'ETL - Update Meta Counts For All Consents'
                }
            }
        }
        stage('Convert from dictionary json to new dictionary input'){
            steps {
                script {
                    build job: 'Old JSON Dictionary to New Dictionary Input Files'
                }
            }
        }
        stage('Dictionary DB Ingest'){
            steps {
                script {
                    build job: 'Dictionary ETL - BDC Ingest Pipeline'
                }
            }
        } 

    }
    post{
        success {
            copyArtifacts(projectName: 'Dictionary ETL - Export DB');
            copyArtifacts(projectName: 'ETL - Generate Phenotype Javabins Data Analyzer');
            archiveArtifacts artifacts: 'db_dump_name.txt', followSymlinks: false;
            archiveArtifacts artifacts: 'docker/pic-sure-hpds-etl/hpds/data_analyzer_javabin_file_name.txt', followSymlinks: false;
        }
    }
}
