definition.script:
pipeline {
    agent any

    stages {
        stage('Upload stigmatizing variable files from git to s3') {
            steps {
                script {
                    build job: 'ETL - Upload stigmatizing variable files from git to s3'
                }
            }
        }
        stage('Dictionary Stigvar Loader') {
            steps {
                script {
                    build job: 'Dictionary ETL - Stigvar Loader'
                    build job: 'Dictionary ETL - Export DB'
                }
            }
        }
        stage('Update stigvars in javabins') {
            steps {
                script {
                    build job: 'ETL - Update static files in javabins', parameters: [
                        string(name: 'S3_DATA_SET_TAR_NAME', value: params.S3_DATA_SET_TAR_NAME),
                         booleanParam(name: 'updateStigvars', value: true), booleanParam(name: 'updateStudyMeta', value: true)]
                }
            }
        }

    }
    post{
        success {
            copyArtifacts(projectName: 'Dictionary ETL - Export DB');
            copyArtifacts(projectName: 'ETL - Update static files in javabins');
            archiveArtifacts artifacts: 'db_dump_name.txt', followSymlinks: false;
            archiveArtifacts artifacts: 'data_analyzer_javabin_file_name.txt', followSymlinks: false;
        }
    }
}
